package ejemplos;

/**
 * 
 */
public class HolaMundo {

	/**
	 * @param args
	 */
	public static void mostrarMensaje(String s) {
		System.out.println(s);
	}
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String mensaje = "Hola Mundo";
		mostrarMensaje(mensaje);
	}

}
